const experiences=[{companyName:"SAP Labs",roleInformation:"6 June 2022 - Present | Software Engineer | Bangalore, India",responsibilties:["Contributed to Central Business Configuration (CBC)."]},{companyName:"Siemens",roleInformation:"1 July 2018 - 1 June 2022 | Software Engineer | Bangalore, India",responsibilties:["Lead the development for 3 edge app projects and took part in the development of 1 embedded system project.","Proactively collaborated with multiple external stakeholders from 4 different geographical locations to ensure smooth progress in the product development.","Implemented clean code using SOLID design principles and software design patterns to adapt easily in an agile and ever changing environment.","Up-skilled the team with Test Driven Development (TDD) and Behavior Driven Development (BDD) practices, which helped us deliver bug free software.","Self taught multiple modern technologies to quickly develop and deliver 1 POC and 2 MVPs which helped us get faster feedback from customers."]}];class ExperienceView{expIndex=0;constructor(){this.displayExperience()}displayExperience(){const e=document.getElementById("experience-list");e.innerHTML="";const n=experiences[this.expIndex],t=document.createElement("h3");t.innerHTML=n.companyName;const i=document.createElement("span");i.innerHTML=n.roleInformation,i.className="duration";const o=document.createElement("ul");o.className="responsibilities";for(const e of n.responsibilties){const n=document.createElement("li");n.innerHTML=e,o.appendChild(n)}e.appendChild(t),e.appendChild(i),e.appendChild(o)}next(){this.hasNext()&&(++this.expIndex,this.displayExperience())}previous(){this.hasPrevious()&&(--this.expIndex,this.displayExperience())}hasNext(){return this.expIndex!=experiences.length-1}hasPrevious(){return 0!=this.expIndex}}!function(){const e=new ExperienceView;function n(){e.hasNext()?e.hasPrevious()||(document.getElementById("left-slide-arrow").style.color="gray",document.getElementById("right-slide-arrow").style.color="black"):(document.getElementById("right-slide-arrow").style.color="gray",document.getElementById("left-slide-arrow").style.color="black")}n();document.getElementById("left-slide-arrow").addEventListener("click",(()=>{e.previous(),n()}));document.getElementById("right-slide-arrow").addEventListener("click",(()=>{e.next(),n()}))}();